      .style("mix-blend-mode", "multiply")
          .attr("fill", d =&gt; {
           sum_target = d3.sum(chords, c =&gt; (c.target.index === d.index) * c.target.value);
           sum_source = d3.sum(chords, c =&gt; (c.source.index === d.index) * c.source.value);
           sum_yht = sum_target-sum_source;
