  function matriisi(names) {
    const index = new Map(names.map((name, i) =&gt; [name, i]));
    const matrix = Array.from(index, () =&gt; new Array(names.length).fill(0));
    for (const {source, target, value} of data) matrix[index.get(source)][index.get(target)] += value;
    return matrix;
  }

 const matrix = matriisi(names)

 const arc = d3.arc()
    .innerRadius(innerRadius)
    .outerRadius(outerRadius)

  //TÄSTÄ ALKAA PIIRROS

  const zoom = d3.zoom()
      .scaleExtent([1, 8])
      .on("zoom", zoomed);

